mapper.select:
		select `company`,`pay_merch`,`out_channel`,`cost_rate`,cost_rate_unit,`qh_rate`,qh_rate_unit,`max_pay_amt`,`min_pay_amt`,`crt_time`,`pay_period`,`if_close`,`pay_channel_type`,`callback_domain`,`capital_pool`,`payment_method`,max_fee,min_fee,clear_ratio from pay_config_company

  		   and company = #{company} 
  		   and pay_merch = #{payMerch} 
  		   and pay_channel_type = #{payChannelType} 
  		   and payment_method = #{paymentMethod} 



 		  			and out_channel = #{outChannel} 

 		  		and out_channel != 'acp'


 		  		and out_channel = 'acp'


  		   and if_close = #{ifClose} 



                order by ${sort} ${order}


                order by crt_time desc



			limit #{offset}, #{limit}

		select count(*) from pay_config_company

	  		 and company = #{company} 
  		 	 and pay_merch = #{payMerch} 
  		  	 and pay_channel_type = #{payChannelType} 
  		  	 and payment_method = #{paymentMethod} 



	 		  			and out_channel = #{outChannel} 

	 		  		and out_channel != 'acp'


	 		  		and out_channel = 'acp'


  		  	 and if_close = #{ifClose}

mapper.insert:
insert into pay_config_company ( `company`, `pay_merch`, `out_channel`, `cost_rate`, cost_rate_unit, `qh_rate`, qh_rate_unit, `max_pay_amt`, `min_pay_amt`, `pay_period`, `if_close`, pay_channel_type, `callback_domain`, capital_pool, payment_method, min_fee, max_fee
,clear_ratio
) values ( #{company}, #{payMerch}, #{outChannel}, #{costRate}, #{costRateUnit}, #{qhRate}, #{qhRateUnit}, #{maxPayAmt}, #{minPayAmt}, #{payPeriod}, #{ifClose}, #{payChannelType}, #{callbackDomain}, #{capitalPool}, #{paymentMethod}, #{minFee}, #{maxFee}
, ${clearRatio}
)

mapper.update:
update pay_config_company
`cost_rate` = #{costRate}, `cost_rate_unit` = #{costRateUnit}, `qh_rate` = #{qhRate}, `qh_rate_unit` = #{qhRateUnit}, `max_pay_amt` = #{maxPayAmt}, `min_pay_amt` = #{minPayAmt}, `pay_period` = #{payPeriod}, `if_close` = #{ifClose}, `pay_channel_type` = #{payChannelType}, `callback_domain` = #{callbackDomain}, `capital_pool` = #{capitalPool}, `payment_method` = #{paymentMethod}, `min_fee` = #{minFee}, `max_fee` = #{maxFee}, `clear_ratio` = #{clearRatio},
where company = #{company} and pay_merch = #{payMerch} and out_channel = #{outChannel}
