mapper.select:
		select `order_no`,`merch_no`,`fee_type`,`order_type`,`pay_merch`,`pay_company`,`out_channel`,`before_amt`,`tran_amt`,`after_amt`,`profit_loss`,`crt_date` from record_pay_merch_bal

		  		   and order_no = #{orderNo} 
		  		   and merch_no = #{merchNo} 
		  		   and fee_type = #{feeType} 
		  		   and order_type = #{orderType} 
		  		   and pay_merch = #{payMerch} 
		  		   and pay_company = #{payCompany} 
		  		   and out_channel = #{outChannel} 
		  		   and profit_loss = #{profitLoss} 
		  		   and crt_date >= #{beginDate} 
				    and crt_date <= #{endDate} 



                order by ${sort} ${order}


                order by order_no desc



			limit #{offset}, #{limit}

select count(*) from record_pay_merch_bal

  		   and order_no = #{orderNo} 
  		   and merch_no = #{merchNo} 
  		   and fee_type = #{feeType} 
  		   and order_type = #{orderType} 
  		   and pay_merch = #{payMerch} 
  		   and pay_company = #{payCompany} 
  		   and out_channel = #{outChannel} 
  		   and profit_loss = #{profitLoss} 
  		   and crt_date >= #{beginDate} 
		    and crt_date <= #{endDate}

mapper.update:
update record_pay_merch_bal
`before_amt` = #{beforeAmt}, `tran_amt` = #{tranAmt}, `after_amt` = #{afterAmt}, `profit_loss` = #{profitLoss},
where order_no = #{orderNo} and merch_no = #{merchNo} and fee_type = #{feeType}
