mapper.select:
		select `order_no`,`merch_no`,`fee_type`,`order_type`,`username`,`before_amt`,`tran_amt`,`after_amt`,`profit_loss`,`crt_date` from record_found_acct

	  		   and order_no = #{orderNo} 
	  		   and crt_date >= #{beginDate} 
	  		    and crt_date <= #{endDate} 
	  		   and merch_no = #{merchNo} 
	  		   and fee_type = #{feeType} 
	  		   and order_type = #{orderType} 
	  		   and username = #{username} 
	  		   and profit_loss = #{profitLoss} 



                order by ${sort} ${order}


                order by crt_date desc



			limit #{offset}, #{limit}

select sum(if(profit_loss=1,tran_amt,-tran_amt)) tranAmt
from record_found_acct

	 and order_no = #{orderNo} 
	 and crt_date >= #{beginDate} 
	  and crt_date <= #{endDate} 
	 and merch_no = #{merchNo} 
	 and fee_type = #{feeType} 
	 and order_type = #{orderType} 
	 and username = #{username} 
	 and profit_loss = #{profitLoss}

	select count(1) from record_found_acct

  		   and order_no = #{orderNo} 
  		   and crt_date >= #{beginDate} 
  		    and crt_date <= #{endDate} 
  		   and merch_no = #{merchNo} 
  		   and fee_type = #{feeType} 
  		   and order_type = #{orderType} 
  		   and username = #{username} 
  		   and profit_loss = #{profitLoss}
